sequence.script:

var payload = mc.getPayloadXML();
var earliestTime = mc.getProperty("uri.var.earliestTime");
var latestTime = mc.getProperty("uri.var.latestTime");
var listDefaultAction = mc.getProperty("uri.var.listDefaultAction");
var orphanField = mc.getProperty("uri.var.orphanField");

if ( earliestTime != null && earliestTime != "") {
    payload.earliest_time = earliestTime ;
}
if ( latestTime != null && latestTime != "") {
    payload.latest_time = latestTime;
}
if ( listDefaultAction != null && listDefaultAction != "") {
    payload.listDefaultActionArgs = listDefaultAction;
}
if ( orphanField != null && orphanField != "") {
    payload.add_orphan_field = orphanField;
}
mc.setPayloadXML(payload);
